mapper.select:
select

from result_user_map

	
		and id = #{id}
	
	
		and user_id = #{user_id}
	
	
		and lat = #{lat}
	
	
		and lng = #{lng}
	
	
		and province = #{province}
	
	
		and address = #{address}
	
	
		and create_time = #{create_time}
	
	
		and version = #{version}

select count(0)
from result_user_map

	
		and id = #{id}
	
	
		and user_id = #{user_id}
	
	
		and lat = #{lat}
	
	
		and lng = #{lng}
	
	
		and province = #{province}
	
	
		and address = #{address}
	
	
		and create_time = #{create_time}
	
	
		and version = #{version}
