mapper.select:
SELECT group_name,MAX(mileage) max,AVG(mileage) avg FROM result_usergroup_drivingtype

	
		and find_in_set(user_type,#{user_type})
	
	
		and group_name = #{group_name}
	
	
		and version = #{version}
	

GROUP BY group_name
ORDER BY sort

set @num := 0, @group_name := '',@user_type := '';
SELECT GROUP_CONCAT(temp1.mileage ORDER BY RAND()) FROM (SELECT user_type,group_name,mileage,@num := if(@group_name=group_name and @user_type=user_type, @num + 1, 1) as row_number,
@group_name := group_name as other_group,@user_type := user_type as other_type FROM `result_usergroup_drivingtype`
ORDER BY group_name,user_type) temp1 

	
		and find_in_set(temp1.user_type,#{user_type})
	
	
		and temp1.group_name = #{group_name}
	
	
		and temp1.version = #{version}
	

GROUP BY temp1.group_name
ORDER BY temp1.group_name ASC

select

from result_usergroup_drivingtype

	
		and id = #{id}
	
	
		and user_type = #{user_type}
	
	
		and group_name = #{group_name}
	
	
		and mileage = #{mileage}
	
	
		and create_time = #{create_time}
	
	
		and version = #{version}

select count(0)
from result_usergroup_drivingtype

	
		and id = #{id}
	
	
		and user_type = #{user_type}
	
	
		and group_name = #{group_name}
	
	
		and mileage = #{mileage}
	
	
		and create_time = #{create_time}
	
	
		and version = #{version}
